mapper.select:
SELECT E.userNo, E.eventNo, E.eventTitle, E.eventContent, E.startDate, E.endDate, E.categoryCode, E.participantLimit, E.noOfParticipants, E.registerDate, EA.cityName, EA.districtName, EA.streetCode, EA.streetName, EA.detail, M.userName AS writer FROM events E INNER JOIN event_address EA ON EA.eventNo = E.eventNo
AND E.categoryCode = #{categoryCode}
INNER JOIN members M ON E.userNo = M.userNo WHERE E.userNo < #{pageInfo.cursorUserNo} ORDER BY EA.eventNo DESC LIMIT #{pageInfo.size}

SELECT EXISTS
        (SELECT applyDate
         FROM participant
         WHERE userNo = #{userNo}
         AND eventNo = #{eventNo}
         AND cancelDate IS NULL
        )
        AS isIdExists

SELECT EXISTS
           (SELECT eventTitle,
                   startDate
            FROM events
            WHERE eventTitle = #{eventTitle}
              AND startDate  = #{startDate}
           )
           AS isEventExists

mapper.update:
UPDATE events
    SET eventTitle = #{eventTitle},
        eventContent = #{eventContent}
WHERE eventNo = #{eventNo}

UPDATE participant
    SET cancelDate = NOW()
WHERE userNo = #{userNo}
    AND eventNo = #{eventNo}
    AND applyDate IS NOT NULL
