mapper.select:
SELECT A.eventTitle, A.eventContent, A.startDate, A.endDate, A.registerDate, A.participantLimit, A.noOfParticipants, B.categoryName, B.cityName, B.districtName, B.streetName, B.detail, B.writer FROM events AS A JOIN (SELECT E.userNo, E.eventNo, E.eventTitle, E.eventContent, E.startDate, E.endDate, E.registerDate, E.participantLimit, E.noOfParticipants, C.categoryName, EA.cityName, EA.districtName, EA.streetName, EA.detail, M.userName AS writer FROM events E LEFT OUTER JOIN event_address EA ON EA.eventNo = E.eventNo LEFT OUTER JOIN category C ON C.categoryCode = E.categoryCode INNER JOIN members M ON E.userNo = M.userNo WHERE E.userNo < #{PageInfo.cursorUserNo}
AND E.categoryCode = #{categotyCode}
ORDER BY registerDate DESC LIMIT #{PageInfo.size} ) AS B WHERE A.eventNo = B.eventNo

SELECT EXISTS
        (SELECT applyDate
         FROM participant
         WHERE userNo = #{userNo}
        )
        AS isIdExists

SELECT EXISTS
           (SELECT eventTitle,
                   startDate
            FROM events
            WHERE eventTitle = #{eventTitle}
              AND startDate  = #{startDate}
           )
           AS isEventExists

mapper.update:
UPDATE events
    SET eventTitle = #{eventTitle}
    AND eventContent = #{eventContent}
WHERE eventNo = #{eventNo}

UPDATE event_address
    SET cityName = #{cityName}
    AND districtName = #{districtName}
    AND streetCode = #{streetCode}
    AND streetName = #{streetName}
    AND detail = #{detail}
WHERE eventNo = #{eventNo}
