mapper.update:
UPDATE members
SET username = #{username}
AND phoneNo  = #{phoneNo}
WHERE userid = #{userId};

UPDATE member_address
SET cityName = #{cityName}
AND districtName = #{districtName}
AND streetName = #{streetName}
WHERE userNo = #{userNo}

UPDATE members
SET password = #{password}
AND updatePwDate = NOW()
WHERE userNo = #{userNo}
