mapper.insert:
insert into
    members
    ( userId
    , username
    , password
    , email
    , confirmEmail
    , phoneNo
    , address
    , userLevel
)
values
    ( #{userId}
    , #{username}
    , #{password}
    , #{email}
    , #{confirmEmail}
    , #{phoneNo}
    , #{address}
    , #{userLevel}
    )

mapper.select:
select EXISTS
           (select userId
            from members
            where userId = #{userId}
           )
           as isIdExists
